mapper.select:
SELECT CONTRACT_DETAIL_NO, CONTRACT_NO, ITEM_CODE, ITEM_NAME , UNIT_OF_CONTRACT, DUE_DATE_OF_CONTRACT, CONTRACT_AMOUNT , UNIT_PRICE_OF_CONTRACT, SUM_PRICE_OF_CONTRACT , MPS_APPLY_STATUS, DELIVERY_STATUS, DESCRIPTION FROM CONTRACT_DETAIL
WHERE DUE_DATE_OF_CONTRACT BETWEEN #{fromDate} AND #{toDate}
WHERE DESCRIPTION IS NULL

mapper.update:
UPDATE CONTRACT_DETAIL SET
CONTRACT_NO = #{contractNo}
, ITEM_CODE = #{itemCode}
, ITEM_NAME = #{itemName}
, UNIT_OF_CONTRACT = #{unitOfContract}
, DUE_DATE_OF_CONTRACT = #{dueDateOfContract}
, CONTRACT_AMOUNT = #{contractAmount}
, UNIT_PRICE_OF_CONTRACT = #{unitPriceOfContract}
, SUM_PRICE_OF_CONTRACT = #{sumPriceOfContract}
, MPS_APPLY_STATUS = #{mpsApplyStatus}
, DELIVERY_STATUS = #{deliveryStatus}
, DESCRIPTION = #{description}
WHERE
CONTRACT_DETAIL_NO = #{contractDetailNo}
